export declare interface AvlMultiSelectControl {
  /**
   * Read: Gets indexes/indices of currently selected elements.
   * Write: After setting the indexes/indices only elements with given indexes/indices will be selected if they exist.
   */
  selectedIndexes: Array<number>;

  /**
   * Read: Gets values of currently selected elements.
   * Write: After setting the values only elements with given values will be selected if they exist.
   */
  selectedValues: Array<object | number | string | undefined>;

}

export declare interface AvlMultiSelectControlEventMap {
  /**
   * Fired when the selectedIndexes property changes, returns the selected indexes in event.detail.value.
   * 
   * @param event Selected indexes/indices are in event.detail.value.
   */
  'selected-indexes-changed': CustomEvent;

  /**
   * Fired when the selectedValues property changes, returns the selected values in event.detail.value.
   * 
   * @param event Selected values are in event.detail.value.
   */
  'selected-values-changed': CustomEvent;
}
