export declare interface AvlSingleSelectControl {
  /**
   * Read: Gets index of currently selected element. Returns -1 if nothing is selected.
   * Write: After setting the index only element with given index will be selected if it exists.
   */
  selectedIndex: number;

  /**
   * Read: Gets value of currently selected element.
   * Write: After setting the value only element with given value will be selected if it exists.
   */
  selectedValue: object | number | string | undefined;

}

export declare interface AvlSingleSelectControlEventMap {

  /**
   * Fired when the selectedIndex property changes, returns the selected index in event.detail.value.
   * 
   * @param event Selected index is in event.detail.value.
   */
  'selected-index-changed': CustomEvent;

  /**
   * Fired when the selectedValue property changes, returns the selected value in event.detail.value.
   * 
   * @param event Selected value is in event.detail.value.
   */
  'selected-value-changed': CustomEvent;

}
